const reducer = (state, action) => {
    switch (action.type) {
        case  "SET_LOADING":
            return {
                ...state,
                isLoading : true,
            };
        case "GET_STORIES":
            return {
                ...state,
                isLoading : true,

                hits : action.payload.hits,
                // nbPages : action.payload.nbPages
            };
    }
    return state
};

export default reducer
