import React from "react";
import { useGlobalContext } from "./context";
import './Stories'


const Stories = () => {
    const {hits, isLoading} = useGlobalContext()
    if(isLoading) {
        return (
            // <>
            // <h1>LOADING</h1>
            // </>
            <div class="spin"></div>
        );
    }

  return (
    <>
  <div>Stories</div>
  {hits.map((curpost) => {
        return <h2>{curpost.title}</h2>
    })
  }
  </>
  );
};

export default Stories;
