import React from "react";
import { useGlobalContext } from "./context";
import './Stories.css'


const Stories = () => {
    const {hits, isLoading} = useGlobalContext()
    if(isLoading) {
        return (
            // <>
            // <h1>LOADING</h1>
            // </>
            <div class="spin-container">
      <div class="spin" id="loader"></div>
      <div class="spin" id="loader2"></div>
      <div class="spin" id="loader3"></div>
      <div class="spin" id="loader4"></div>
      <span id="text">LOADING...</span>
    </div>
        );
    }

  return (
    <>
  {hits.map((curpost) => {
    const {title, author, objectID, url, num_comments} = curpost
        return <div class="blog_post">
        <div class="container_copy">
          <h1>{title}</h1>
          {/* <p>The position property specifies the type of positioning method used for an element (static, relative, absolute, fixed, or sticky).</p> */}
          <h3> By {author}</h3>
          <h4>{num_comments} comments`}</h4>
          <a class="btn_primary" href={url} target="_blank">Read More</a>
        </div>
      </div>
    })
  }
  </>
  );
};

export default Stories;
